from pyqtgraph.widgets.RawImageWidget import RawImageWidget


class InteractiveImageWidget(RawImageWidget):
    def __init__(self, parent=None, scaled=False):
        super().__init__(parent=parent)
        self.label = None
        self.renderer = None
        self.initUI()

    def initUI(self):
        self.label = QLabel(self)
        self.label.setStyleSheet("QLabel { background-color : white; color : black; }")
        self.label.hide()
        self.setMouseTracking(True)

    def setRenderer(self, renderer):
        self.renderer = renderer

    def mouseMoveEvent(self, event):
        x, y = event.position().x(), event.position().y()
        if self.is_over_target_pixel(x, y):
            self.show_information(x, y)
        else:
            self.label.hide()

    def is_over_target_pixel(self, x, y):

        # Implement logic to determine if (x, y) is over the target pixel or area
        return (int(x), int(y)) in self.renderer.mdi_map  # Replace with your own logic

    def show_information(self, x, y):
        # Display information as a tooltip or in a label
        self.label.setText(str(self.renderer.mdi_map[(int(x), int(y))]))
        self.label.move(int(x) + 10, int(y) + 10)
        self.label.show()
